mapper.select:
select
u.*
from tab_videoxuser u


        AND u.userId = #{userId}


        AND u.videoId = #{videoId}


        AND u.id = #{id}


        AND u.isCollection = #{isCollection}

select
u.*,r.title,r.avid,r.owner,r.ownerId,r.pic,r.remark,r.hot,r.cid,r.good,r.bad,r.collection
from tab_videoxuser u left join tab_video r on u.videoId = r.id LEFT JOIN tab_user p on p.id = u.userId

    u.status <> 9

        AND p.openId = #{openId}


        AND u.userId = #{userId}


        AND u.isCollection = #{isCollection}


order by u.updatetime desc
